import React, {Component} from 'react';
import {connect} from 'react-redux';
import {getAllProducts, getProductsByInsurance, getAllBusiness} from './../../actions';

class Product extends Component{

    async componentDidMount(){
        if(this.props.params.insurance){
            await this.props.getProductsByInsurance(this.props.params.insurance._id);
        }else{
            await this.props.getAllProducts();
        }
        await this.props.getAllBusiness();
    }

    render(){
        return <div>PRODUCTS</div>
    }

    buildProductManager = () => {
        if(this.props.products.length === 0 || this.props.businessList.length === 0) return;
    }
}

const mapStateToProps = (state) => {
    return {
        products: state.product.products,
        businessList: state.business.businesses
    }
}

export default connect(mapStateToProps, {getAllProducts, getProductsByInsurance, getAllBusiness})(Product);
