import React, {Component} from 'react';
import Input from './Input';

class Form extends Component{
    constructor(props){
        super(props);
        this.questionsPerPage = 3;
        this.currentPage = 1;
    }

    render(){
        return (
            <div class="modal fade" id="modalForm" tabindex="-1" role="dialog" aria-labelledby="myModalLabel"
                aria-hidden="true">
                    <div class="modal-dialog" role="document">
                        <div class="modal-content">
                            <div class="modal-header text-center">
                                <h4 class="modal-title w-100 font-weight-bold">Seguro de Vehiculo</h4>
                                <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                                    <span aria-hidden="true">&times;</span>
                                </button>
                            </div>
                            <div class="modal-body mx-3">
                                {this.renderQuestions()}
                            </div>
                        </div>
                    </div>
            </div>
        );
    }

    renderQuestions(){
        let questions = [];
        let initialIndex = (this.currentPage-1) * this.questionsPerPage;
        let i;
        let finalIndex = initialIndex + this.questionsPerPage;

        for(i = initialIndex; i < finalIndex; i++){
            if(i < finalIndex){
              questions.push(<Input question = {this.props.questions[i]}/>)  
            }
        }

        return questions;
    }
}

export default Form;
