import React, {Component} from 'react';
import {Grid} from '@material-ui/core';

import TextInput from './TextInput';
import RadioInput from './RadioInput';
import SelectInput from './SelectInput';
import CheckBoxInput from './CheckBoxInput';
import DateInput from './DateInput';

class Input extends Component{
    render(){
        return this.renderInput()
    }

    renderInput = () => {
        let element;

        switch(this.props.question.inputType.tag){
            case 'input': 
            case 'textarea':
                element = <TextInput className = 'input-container' question = {this.props.question} onStateChange = {this.props.onStateChange} requiredQuestion = {this.props.requiredQuestion} index = {this.props.index}/>
            break;

            case 'radio':
                element = <RadioInput className = 'input-container' question = {this.props.question} onStateChange = {this.props.onStateChange} requiredQuestion = {this.props.requiredQuestion} index = {this.props.index}/>
            break;

            case 'select':
                element = <SelectInput className = 'input-container' question = {this.props.question} onStateChange = {this.props.onStateChange} requiredQuestion = {this.props.requiredQuestion} index = {this.props.index}/>
            break;

            case 'checkbox':
                element = <CheckBoxInput className = 'input-container' question = {this.props.question} onStateChange = {this.props.onStateChange} requiredQuestion = {this.props.requiredQuestion} index = {this.props.index}/>
            break;

            case 'calendar':
                element = <DateInput className = 'input-container' question = {this.props.question} onStateChange = {this.props.onStateChange} requiredQuestion = {this.props.requiredQuestion} index = {this.props.index}/>
            break;

            default:
                element = null
            break;
        }

        return element;
    }
}

export default Input;
