import React, {Component} from 'react';
import CoverModal from './CoverModal';

class ProductCard extends Component{
    constructor(props){
        super(props);
    }   

    render(){
        return (
            <div>
                <div class="card" style = {this.getCardStyle()}>
                    <h5 class="card-header">{this.props.business}</h5>
                    <div class="card-body">
                        <h5 class="card-title">{this.props.insurance}</h5>
                        <p class="card-text">Prima Anual: HNL {this.props.payment}</p>
                        <a href="#" class="btn btn-primary">Ver Cobertura</a>
                    </div>
                </div> 
                <CoverModal 
                    onCloseModal = {this.hideCovers}
                /> 
            </div>

        );
    }

    showCovers(e){
        e.preventDefault();
        this.setState({showModal: true});
    }

    hideCovers(){
        this.setState({showModal: false});
    }

    getCardStyle(){
        return {
            width: '700px',
            marginLeft: '300px',
            marginBottom: '50px',
            border: '2px solid'
        }
    }
}


export default ProductCard;
