import './../../styles/SelectInput.css';
import React, {Component} from 'react';
import {FormLabel, Select, MenuItem, FormControl} from '@material-ui/core';

class SelectInput extends Component{
    constructor(props){
        super(props);
        this.state = {
            value: '', 
            open: false
        }
    }

    handleClose = () => {
        this.setState({open: false});
    }

    handleOpen = () => {
        this.setState({open: true});
    }

    handleChange = (event) => {
        let {value} = event.target;
        this.setState({value});
    }

    render(){
        return <FormControl className = 'select-input'>
                    <FormLabel component="legend">{this.props.question.question}</FormLabel>
                    <Select
                        labelId="demo-controlled-open-select-label"
                        id="demo-controlled-open-select"
                        open={this.state.open}
                        onClose={this.handleClose}
                        onOpen={this.handleOpen}
                        value={this.state.value}
                        onChange={this.handleChange}
                        className = 'select-component'
                    >
                        <MenuItem value="">
                            <em>None</em>
                        </MenuItem>
                        {this.renderOptions()}
                    </Select>
                </FormControl>
    }

    renderOptions = () => {
        return this.props.question.inputType.options.map((option, index) => {
            return <MenuItem value={option} key = {'s' + index}>{option}</MenuItem>
        })
    }
}

export default SelectInput;
