import '../styles/Tab.css';

import React, {Component, createRef} from 'react';
import {connect} from 'react-redux';
import {ListItem, ListItemText} from '@material-ui/core';
import {selectTab} from '../actions';

class Tab extends Component{
    constructor(props){
        super(props);
        this.tabRef = createRef();
    }
    render(){
        return <ListItem 
                button 
                className = {`sidebar-tab ${this.props.focused === true? 'focused': ''}`}
                ref = {this.tabRef}
                onClick = {this.handleClick}
                onMouseOver = {this.handleHover}>
            <ListItemText primary = {this.props.tab.text}/>
        </ListItem>;
    }

    handleClick = () => {
        this.props.selectTab(this.props.index);
    }

    handleHover = () => {
        if(this.props.focused === true) return;
        this.tabRef.current.style.backgroundColor = '#111111';
    }
}



export default connect(null, {selectTab})(Tab);
