import React, {Component} from 'react';
import { Radio, RadioGroup, FormControlLabel, FormControl, FormLabel } from '@material-ui/core';

class RadioInput extends Component{
    constructor(props){
        super(props);
        this.state = {
            value: props.question.inputType.options[0]
        }
    }

    handleChange = (event) => {
        let {value} = event.target;
        this.setState({value});
    }
    render(){
        return <FormControl component="fieldset">
                    <FormLabel component="legend">{this.props.question.question}</FormLabel>
                    <RadioGroup aria-label="gender" name="gender1" value={value} onChange={handleChange}>
                        {this.props.renderOptions()}
                    </RadioGroup>
                </FormControl>
    }

    renderOptions = () => {
        return this.props.question.inputType.options.map((option, index) => {
            return <FormControlLabel value={option} control={<Radio />} label={option} />
        })
    }
}

export default RadioInput;
