import React, {Component} from 'react';
import {connect} from 'react-redux';
import {getAllProducts, getProductsByInsurance, getAllBusiness, getCoversByProduct} from './../../actions';

class Product extends Component{
    constructor(props){
        super(props);
        this.manager = [];
    }

    async componentDidMount(){
        if(this.props.params.insurance){
            await this.props.getProductsByInsurance(this.props.params.insurance._id);
        }else{
            await this.props.getAllProducts();
        }
        await this.props.getAllBusiness();
    }

    render(){
        this.buildProductManager();
        return <div>PRODUCTS</div>
    }

    buildProductManager = async () => {
        if(this.props.products.length === 0 || this.props.businessList.length === 0) return;

        this.manager = this.props.products.map(async product => {
            let newProduct = Object.assign({}, product);
            newProduct.business = this.props.businessList.find(business => product.business === business._id);
            await this.props.getCoversByProduct(product._id);
            newProduct.covers = [this.props.covers];
            return newProduct;
        });
    }
}

const mapStateToProps = (state) => {
    return {
        products: state.product.products,
        businessList: state.business.businesses, 
        covers: state.cover.covers
    }
}

export default connect(mapStateToProps, {getAllProducts, getProductsByInsurance, getAllBusiness, getCoversByProduct})(Product);
